mapper.select:
SELECT hourly 
FROM `assignment` a 
WHERE a.assign_code = #{assign_code};

SELECT IF(e.resign_date IS NULL , a.monthly , (SELECT (SELECT COUNT(*) FROM attendance a JOIN att_code ac ON a.atten_code = ac.atten_code JOIN employee e ON a.emp_code = e.emp_code WHERE a.emp_code = #{emp_code} AND (a.atten_code = 1 OR a.atten_code = 3)
AND YEAR(work_date) = YEAR(CURDATE()) - 1 AND MONTH(work_date) = 12
AND YEAR(work_date) = YEAR(CURDATE()) AND MONTH(work_date) = MONTH(CURDATE()) - 1
) * 8 * a.hourly ) ) monthly FROM `assignment` a JOIN employee e ON a.assign_code = e.assign_code WHERE a.assign_code = #{assign_code} AND emp_code = #{emp_code};

SELECT COUNT(*) FROM pay p WHERE emp_code = #{emp_code}
AND pay_year = YEAR(CURDATE()) - 1 AND pay_month = 12
AND pay_year = YEAR(CURDATE()) AND pay_month = MONTH(CURDATE()) - 1

mapper.insert:
		INSERT INTO pay(emp_code
			, pay_year
			, pay_month
			, give_date
 			, pay_over
			, pay_meal
			, absence
			, pay_amount
			, income_tax
			, resident_tax
			, national_tax 
			, emp_insurance
			, health_insurance
			, longcare_insurance
			, actual_pay 
			, reg_name 
		)
		VALUES (#{emp_code}
			, IF(MONTH(CURDATE())-1 = 0, YEAR(CURDATE())-1, YEAR(CURDATE()) )
			, IF(MONTH(CURDATE())-1 = 0, 12, MONTH(CURDATE())-1 )
			, #{give_date}
 			, #{pay_over}
			, #{pay_meal}
			, #{absence}
			, #{pay_amount}
			, #{income_tax}
			, #{resident_tax}
			, #{national_tax}
			, #{emp_insurance}
			, #{health_insurance}
			, #{longcare_insurance}
			, #{actual_pay} 
			, "오명진"
		);
